import React from 'react';
import { useSelector } from 'react-redux';
import CartBtn from './buttons/CartBtn';
import Login from './buttons/Login';
import Signup from './buttons/Signup';
import './sidebar.css';

const Sidebar = ({ isOpen, toggleSidebar }) => {
    const cartItems = useSelector((state) => state.addItem);

    return (
        <div className={`sidebar ${isOpen ? 'open' : ''}`}>
            <button className="close-btn" onClick={toggleSidebar}>&times;</button>
            
            <h2>Giỏ hàng</h2>
            {cartItems.length === 0 ? (
                <p>Giỏ hàng của bạn đang trống.</p>
            ) : (
                <ul>
                    {cartItems.map((item, index) => (
                        <li key={index}>
                            <span>{item.name}</span> - {item.quantity} x {item.price} 
                            <div className="action-buttons">
                                <button className="btn btn-danger">Xóa</button>
                                <button className="btn btn-primary">Chỉnh sửa</button>
                            </div>
                        </li>
                    ))}
                </ul>
            )}

            <h2>Tài khoản</h2>
            {/* Render Login and Signup components directly in the sidebar */}
            <Login />
            <Signup />
        </div>
    );
};

export default Sidebar;
