// Sidebar.js
import React from 'react';
import { NavLink } from 'react-router-dom';
import { useSelector } from 'react-redux';
import Login from './button/Login';
import Signup from './button/Signup';
import './sidebar.css';


const Sidebar = ({ isOpen, toggleSidebar }) => {
    const cartItems = useSelector((state) => state.addItem);

    return (
        <div className={`sidebar ${isOpen ? 'open' : ''}`}>
            <button className="close-btn" onClick={toggleSidebar}>
                &times;
            </button>
            <h2>Giỏ hàng</h2>
            {cartItems.length === 0 ? (
                <p>Giỏ hàng của bạn đang trống.</p>
            ) : (
                <ul>
                    {cartItems.map((item, index) => (
                        <li key={index}>
                            <span>{item.name}</span> - {item.quantity} x {item.price} 
                            <button className="btn btn-danger">Xóa</button>
                            <button className="btn btn-primary">Chỉnh sửa</button>
                        </li>
                    ))}
                </ul>
            )}
            <h2>Tài khoản</h2>
            <NavLink to="#" data-bs-toggle="modal" data-bs-target="#loginModal">
                Đăng nhập
            </NavLink>
            <br />
            <NavLink to="#" data-bs-toggle="modal" data-bs-target="#signupModal">
                Đăng ký
            </NavLink>
        </div>
    );
};

export default Sidebar;
